/*Table structure for table `systemnews` */

DROP TABLE IF EXISTS `systemnews`;

CREATE TABLE `systemnews` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '新闻编号',
  `m_id` int(11) DEFAULT NULL COMMENT '发布者（管理员）',
  `title` varchar(100) DEFAULT NULL COMMENT '标题',
  `body` varchar(1000) DEFAULT NULL COMMENT '正文',
  `publishAt` date DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
