/*Table structure for table `user_order` */

DROP TABLE IF EXISTS `user_order`;

CREATE TABLE `user_order` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '订单编号',
  `u_id` int(11) DEFAULT NULL COMMENT '用户编号',
  `r_id` int(11) DEFAULT NULL COMMENT '取车人编号',
  `c_id` int(11) DEFAULT NULL COMMENT '车辆编号',
  `startAt` date DEFAULT NULL COMMENT '订单开始时间',
  `endAt` date DEFAULT NULL COMMENT '订单结束时间',
  `backAt` date DEFAULT NULL COMMENT '还车时间',
  `orderStatus` int(11) DEFAULT NULL COMMENT '订单状态',
  `orderCheck` int(11) DEFAULT NULL COMMENT '订单审核状态',
  `payStatus` int(11) DEFAULT NULL COMMENT '订单支付状态',
  `total` float(10,2) DEFAULT NULL COMMENT '订单总价',
  `tackAt` date DEFAULT NULL COMMENT '下单时间',
  `backStatus` int(11) DEFAULT NULL COMMENT '还车状态',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=86 DEFAULT CHARSET=utf8;

